    function beforeUnSave(t,c){
       var b={};
       b.id='bsave';
          if(Ext.getCmp(t.formName).isDirty()){
    		Ext.Msg.show({
    		    title:'ÌáÊ¾',
    		    message: 'ÊÇ·ñÐèÒª±£´æ?',
    		   buttons: Ext.Msg.YESNOCANCEL,
    		    icon: Ext.Msg.QUESTION,
    		    fn: function(btn) {
    		        if (btn === 'yes') {
    		           if(CurPageHandler(b)){
    		             t.remove(c,true);
    		             }
         	        } else if (btn === 'no') {
    		           dirtyInit(Ext.getCmp(t.formName));t.remove(c,true);
    		        } else {} 
    		    }
    		}); 
    	   return false;
        }
    }
